mapper.select:

select 

from edge
where stkcdA = #{stkcda,jdbcType=VARCHAR}
  and stkcdB = #{stkcdb,jdbcType=VARCHAR}
  and graphId = #{graphid,jdbcType=VARCHAR}

mapper.delete:

delete from edge
where stkcdA = #{stkcda,jdbcType=VARCHAR}
  and stkcdB = #{stkcdb,jdbcType=VARCHAR}
  and graphId = #{graphid,jdbcType=VARCHAR}

mapper.insert:

insert into edge (stkcdA, stkcdB, graphId, 
  status, propagateProbA, propagateProbB
  )
values (#{stkcda,jdbcType=VARCHAR}, #{stkcdb,jdbcType=VARCHAR}, #{graphid,jdbcType=VARCHAR}, 
  #{status,jdbcType=TINYINT}, #{propagateproba,jdbcType=DOUBLE}, #{propagateprobb,jdbcType=DOUBLE}
  )

mapper.update:

update edge


    status = #{status,jdbcType=TINYINT},


    propagateProbA = #{propagateproba,jdbcType=DOUBLE},


    propagateProbB = #{propagateprobb,jdbcType=DOUBLE},


where stkcdA = #{stkcda,jdbcType=VARCHAR}
  and stkcdB = #{stkcdb,jdbcType=VARCHAR}
  and graphId = #{graphid,jdbcType=VARCHAR}


update edge
set status = #{status,jdbcType=TINYINT},
  propagateProbA = #{propagateproba,jdbcType=DOUBLE},
  propagateProbB = #{propagateprobb,jdbcType=DOUBLE}
where stkcdA = #{stkcda,jdbcType=VARCHAR}
  and stkcdB = #{stkcdb,jdbcType=VARCHAR}
  and graphId = #{graphid,jdbcType=VARCHAR}
